static char *str = 
"";
void shell8(void)
{
    int i, err;

    //clear count
    SOC_TIMER = 0;
    // asm volatile("mtc0 $0, $9");

    err=0;
    printf("sha test begin.\n");
    if(SIMU_FLAG){
        err = sha_driver(str);
    }else{
        for(i=0; i<LOOPTIMES; i++)
            err += sha_driver(str);
    }

	if(err == 0){
        printf("sha PASS!\n");
		*((int *)LED_RG1_ADDR) = 1;  
		*((int *)LED_RG0_ADDR) = 1;  
		*((int *)LED_ADDR)     = 0xffff;  
	}else{
        printf("sha ERROR!!!\n");
		*((int *)LED_RG1_ADDR) = 1;  
		*((int *)LED_RG0_ADDR) = 2;  
		*((int *)LED_ADDR)     = 0;
	}

    SOC_NUM = total_count_my_sha;  
    *((volatile unsigned *)CONFREG_CR0) = total_count_my_sha;  
    *((volatile unsigned *)CONFREG_CR1) = total_count_sha;  
	printf("sha: Total Count(SoC count) = 0x%x\n", total_count_sha);
	printf("sha: Total Count(CPU count) = 0x%x\n", total_count_my_sha);

    return;
}
